import React, { useState, useEffect } from "react";
import { MapContainer, Popup, TileLayer, Circle, Marker } from 'react-leaflet';
import * as L from 'leaflet';
import {Icon} from 'leaflet';
import './leaflet.css';
import 'leaflet/dist/leaflet.css';
import 'leaflet-defaulticon-compatibility/dist/leaflet-defaulticon-compatibility.webpack.css'; // Re-uses images from ~leaflet package
import 'leaflet-defaulticon-compatibility';
import mmarker from '../components/icons/pin-location-4354.png';
import useSWR from 'swr'

const myIcon = new L.icon({
    iconUrl: mmarker ,
    iconSize:[35,35],
})

const fetcher = (...args) => fetch(...args)

const position =  [60.1699, 24.9384]

function Leaflet(){
    const url = `https://api.weatherbit.io/v2.0/current/airquality?lat=60.1699&lon=24.9384&key=b7c5d1e0507348789b52b6e2be32f5ec`;
    const { data, error } = useSWR(url, { fetcher });
    // const [data,setData] = useState({})
    // const [lat, setLat] = useState('')
    // const [long, setLong] = useState('')
    return(
        
        <MapContainer center={position} zoom={13} scrollWheelZoom={false} className="map_container">
            
        <TileLayer
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />
        <Marker icon={myIcon} position={position}>
          <Popup>
            A pretty CSS3 popup. <br /> Easily customizable.
          </Popup>
        </Marker>
      </MapContainer>
    )
   


}
export default Leaflet;
