import React, { useState, useEffect } from "react";
import { MapContainer, Popup, TileLayer, Circle, Marker } from 'react-leaflet';
import * as L from 'leaflet';
import {Icon} from 'leaflet';
import './leaflet.css';
import 'leaflet/dist/leaflet.css';
import 'leaflet-defaulticon-compatibility/dist/leaflet-defaulticon-compatibility.webpack.css'; // Re-uses images from ~leaflet package
import 'leaflet-defaulticon-compatibility';
import mmarker from '../components/icons/Location-marker.svg'
delete L.Icon.Default.prototype._getIconUrl;

L.Icon.Default.mergeOptions({
    iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png'),
    iconUrl: require('leaflet/dist/images/marker-icon.png'),
    shadowUrl: require('leaflet/dist/images/marker-shadow.png'),
  });

//const url = `https://api.weatherbit.io/v2.0/current/airquality?lat=${lat}&lon=${long}&key=b7c5d1e0507348789b52b6e2be32f5ec`
const myIcon = new L.icon({
    iconUrl: mmarker ,
    iconSize:[25,25],
})

const position =  [60.1699, 24.9384]
function leaflet(){
    return(
        
        <MapContainer center={position} zoom={13} scrollWheelZoom={false} className="map_container">
        <TileLayer
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />
        <Marker icon={myIcon} position={position}>
          <Popup>
            A pretty CSS3 popup. <br /> Easily customizable.
          </Popup>
        </Marker>
      </MapContainer>
    )
   


}
export default leaflet;
