import React, { useState, useEffect } from "react";
import { MapContainer, Popup, TileLayer, Circle} from 'react-leaflet';
import L from 'leaflet';
import {Icon, Marker} from 'leaflet';
import './leaflet.css';
import 'leaflet/dist/leaflet.css';


//const url = `https://api.weatherbit.io/v2.0/current/airquality?lat=${lat}&lon=${long}&key=b7c5d1e0507348789b52b6e2be32f5ec`
const myIcon = Icon({
    iconUrl: '../components/icons/18337528831606062173-128.png',
    iconSize:[25,25],
})

const position =  [60.1699, 24.9384]
function leaflet(){
    return(
        
        <MapContainer center={position} zoom={13} scrollWheelZoom={false} className="map_container">
        <TileLayer
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />
        <Marker icon={myIcon} position={position}>
          <Popup>
            A pretty CSS3 popup. <br /> Easily customizable.
          </Popup>
        </Marker>
      </MapContainer>
    )
   


}
export default leaflet;
